import { createReducer, on } from "@ngrx/store";
import { closeSidePanel, openSidePanel } from "./home.actions";
export const initialState = {
  sidePanel: false
};
const featureReducer = createReducer(initialState, on(openSidePanel, state => ({ ...state,
  sidePanel: true
})), on(closeSidePanel, state => ({ ...state,
  sidePanel: false
})));
export function reducerHome(state, action) {
  return featureReducer(state, action);
}
